import React, { useState } from 'react';

import { useHistory } from 'react-router-dom';


const FrontPage= () => {
  const [selectedRole, setSelectedRole] = useState('');



  const handleClick = (role) => {
    const history = useHistory();
    history.push(`/${role}`);
  };


  return (
    <div>
      <h1>Welcome to the Login Portal</h1>
      <p>Please select your role:</p>
      <ul>
        <li>
          <button onClick={() => handleClick('user')}>User</button>
        </li>
        <li>
          <button onClick={() => handleClick('user')}>Tourism Agent</button>
        </li>
        <li>
          <button onClick={() => handleClick('user')}>Airline Agent</button>
        </li>
        <li>
          <button onClick={() => handleClick('user')}>Flight Attendant</button>
        </li>
        <li>
          <button onClick={() => handleClick('user')}>System Admin</button>
        </li>
      </ul>

    </div>
  );
};

export default FrontPage;
