import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';


const FrontPage= () => {
  const [selectedRole, setSelectedRole] = useState('');

const handleClick = (role) => {
    navigate('/goodbye');
    setSelectedRole(role);}

  return (
    <div>
      <h1>Welcome to the Login Portal</h1>
      <p>Please select your role:</p>
      <ul>
        <li>
          <button onClick={() => handleRoleSelection('user')}>User</button>
        </li>
        <li>
          <button onClick={() => handleRoleSelection('tourismAgent')}>Tourism Agent</button>
        </li>
        <li>
          <button onClick={handleClick}>Airline Agent</button>
        </li>
        <li>
          <button onClick={() => handleRoleSelection('flightAttendant')}>Flight Attendant</button>
        </li>
        <li>
          <button onClick={() => handleRoleSelection('systemAdmin')}>System Admin</button>
        </li>
      </ul>

    </div>
  );
};

export default FrontPage;
