import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Link, Redirect } from 'react-router-dom';

// Separate components for different login pages
const Login = () => {
  // Implement user login logic here
  return <div>User Login Page</div>;
};

const TourismAgentLogin = () => {
  // Implement tourism agent login logic here
  return <div>Tourism Agent Login Page</div>;
};

const AirlineAgentLogin = () => {
  // Implement airline agent login logic here
  return <div>Airline Agent Login Page</div>;
};

const FlightAttendantLogin = () => {
  // Implement flight attendant login logic here
  return <div>Flight Attendant Login Page</div>;
};

const SystemAdminLogin = () => {
  // Implement system admin login logic here
  return <div>System Admin Login Page</div>;
};

const LandingPage = () => {
  const [selectedRole, setSelectedRole] = useState('');

  const handleRoleSelection = (role) => {
    setSelectedRole(role);
  };

  return (
    <div>
      <h1>Welcome to the Login Portal</h1>
      <p>Please select your role:</p>
      <ul>
        <li>
          <button onClick={() => handleRoleSelection('user')}>User</button>
        </li>
        <li>
          <button onClick={() => handleRoleSelection('tourismAgent')}>Tourism Agent</button>
        </li>
        <li>
          <button onClick={() => handleRoleSelection('airlineAgent')}>Airline Agent</button>
        </li>
        <li>
          <button onClick={() => handleRoleSelection('flightAttendant')}>Flight Attendant</button>
        </li>
        <li>
          <button onClick={() => handleRoleSelection('systemAdmin')}>System Admin</button>
        </li>
      </ul>
      {selectedRole && <Redirect to={`/${selectedRole}`} />}
    </div>
  );
};
